import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";
import companyDetails from "../config/companyDetails.js";

const logoBase64 =
  "";

const signature =
  "";

const generateInvoicePDF = (
  invoiceData,
  clientData,
  bankDetails,
  companyDetails
) => {
  const doc = new jsPDF();
  const currencySymbol = invoiceData.currency === "USD" ? "$" : "Rs. ";

  // Watermark removed as per user request

  // Logo
  doc.addImage(logoBase64, "PNG", 10, 15, 75, 15);

  // Company Header 
  doc.setFontSize(11);
  doc.setFont("helvetica", "bold");
  doc.text(companyDetails.name || "", 200, 15, { align: "right" });
  doc.setFontSize(8);
  doc.setFont("helvetica", "normal");
  doc.text(companyDetails.address || "", 200, 20, { align: "right" });
  doc.text(
    `Contact: ${companyDetails.contact || ""} Email: ${
      companyDetails.email || ""
    }`,
    200,
    25,
    { align: "right" }
  );
  doc.text(
    `GST: ${companyDetails.gst_number || ""} LUT: ${
      companyDetails.LUTNumber || ""
    }`,
    200,
    30,
    { align: "right" }
  );

  doc.line(10, 42, 200, 42);

  // Client Details
  doc.setFontSize(12);
  doc.setFont("helvetica", "bold");
  doc.text("To,", 14, 50);
  doc.setFont("helvetica", "normal");
  doc.text(clientData.name, 14, 56);
  doc.setFontSize(10);
  const address = `${clientData.address.street}, ${clientData.address.city}, ${clientData.address.state}, ${clientData.address.pincode}`;
  const addressLines = doc.splitTextToSize(address, 100);
  let yPosition = 62;
  addressLines.forEach((line) => {
    doc.text(line, 14, yPosition);
    yPosition += 6;
  });
  yPosition += 1; // Margin
  doc.text(`GSTIN: ${clientData.gst_number || "N/A"}`, 14, yPosition);

  // Invoice Details
  doc.setFont("helvetica", "bold");
  doc.text(`INVOICE NO: ${invoiceData.invoice_number}`, 195, 50, {
    align: "right",
  });

  // Safe date formatting
  const formatDate = (dateStr) => {
    if (!dateStr) return "N/A";
    try {
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) return "N/A";
      return date.toLocaleDateString("en-IN", {
        year: "numeric",
        month: "short",
        day: "numeric",
      });
    } catch {
      return "N/A";
    }
  };

  doc.text(`Date: ${formatDate(invoiceData.issue_date)}`, 195, 56, {
    align: "right",
  });

  // === MODIFICATION 1: Services Table ===
  const tableRows = invoiceData.services.map((s) => ({
    description: s.description,
    hsc_sac_code: s.hsc_sac_code || "",
    amount: `${currencySymbol}${s.amount.toFixed(2)}`,
  }));

  // Add summary rows
  tableRows.push(
    {
      description: "Sub Total",
      hsc_sac_code: "",
      amount: `${currencySymbol}${invoiceData.subtotal.toFixed(2)}`,
    },
    // Changed "GST" to "IGST" as per the complete image
    {
      description: `IGST ${invoiceData.gst_percentage}%`,
      hsc_sac_code: "",
      amount: `${currencySymbol}${invoiceData.gst_amount.toFixed(2)}`,
    },
    {
      description: "Total",
      hsc_sac_code: "",
      amount: `${currencySymbol}${invoiceData.total_amount.toFixed(2)}`,
    }
  );

  autoTable(doc, {
    startY: yPosition + 10,
    head: [["Description of Service", "HSC / SAC code", "Amount"]],
    body: tableRows.map((row) => [
      row.description,
      row.hsc_sac_code,
      row.amount,
    ]),
    theme: "plain",
    styles: {
      font: "helvetica",
      fontSize: 10,
      textColor: [0, 0, 0],
      lineColor: [0, 0, 0],
      lineWidth: 0.1,
      overflow: "linebreak",
      cellPadding: 2,
    },
    headStyles: {
      fillColor: [255, 255, 255],
      fontStyle: "bold",
      halign: "left",
      minCellHeight: 8,
      overflow: "visible",
    },
    columnStyles: {
      0: { cellWidth: 95 },
      1: { cellWidth: 35, halign: "center" },
      2: { cellWidth: 40, halign: "right" },
    },
  });

  const finalY = doc.lastAutoTable.finalY + 10;

  // === MODIFICATION 2: Bank Details ===
  doc.setFont("helvetica", "bold");
  doc.text("BANK DETAILS:", 14, finalY);
  doc.setFont("helvetica", "normal");
  doc.setFontSize(8);

  const bankData = [
    ["Name", bankDetails.accountHolderName],
    ["A/C Number", bankDetails.accountNumber],
    ["IFSC", bankDetails.ifscCode],
    ["Bank", bankDetails.bankName],
    ["Type of Account", bankDetails.accountType || "N/A"], // Added
    ["Swift Code", bankDetails.swiftCode || "N/A"], // Added
  ];

  autoTable(doc, {
    startY: finalY + 5,
    body: bankData,
    theme: "plain",
    styles: { fontSize: 8, lineWidth: 0.1, lineColor: [0, 0, 0] },
    columnStyles: { 0: { cellWidth: 30 } },
    tableWidth: 90,
    margin: { left: 14 },
  });

  // === MODIFICATION 3: Signature ===
  doc.setFont("helvetica", "bold");
  doc.setFontSize(10);
  // Added "For, [Company Name]"
  doc.text(`For, ${companyDetails.name}`, 190, finalY, { align: "right" });

  // Added Signature Image
  doc.addImage(signature, "PNG", 122, finalY - 1, 100, 30, { align: "right" });

  doc.setFont("helvetica", "normal");
  doc.text("(Authorized Signatory)", 190, finalY + 30, { align: "right" });

  return doc.output("dataurlstring").split(",")[1];
};

export { generateInvoicePDF };
